<script setup lang="ts">
import { initCustomFormatter, Ref, ref } from "vue";
import ServerList from "./components/server-list.vue";
import Chat from "./components/chat.vue";
import Login from "./components/Login.vue";
import ChannelList from "./components/channel-list.vue";
import UserList from "./components/user-list.vue";
import Settings from "./components/Settings.vue";
import { login, User  } from "./models/user";
import { Theme } from "./models/theme";
import { Channel, Server } from "./models/Server";
import { useStore } from "vuex";
import { invoke } from '@tauri-apps/api/tauri'
import { listen } from '@tauri-apps/api/event'
import { createUser } from './models/user'
import Checkbox from './components/Checkbox.vue'
import Cropper from "./components/Cropper.vue";

const store = useStore();

let dropdown = ref();
let chat = ref();

const showDropdown = (x: string, y: string, user: User) => {
    selectedUser.value = user;
    (dropdown.value as HTMLElement).style.left = x;
    (dropdown.value as HTMLElement).style.top = y;
    (dropdown.value as HTMLElement).classList.add("visible");
}

const hideDropdown = () => {
    (dropdown.value as HTMLElement).classList.remove("visible");
}

document.body.addEventListener('click', (event) => {
    if (event.target != dropdown.value! && !(dropdown.value! as HTMLElement).contains(event.target as Node)) {
        hideDropdown();
    }
}, true); 

let themes = [
    {
        name: 'Default',
        bg: "#293241",
        fg: "#3D5A80",
        tooltip: "#161617",
        text: "#E0FBFC",
        buttonColor: "rgba(40, 40, 40, 0.3)",
        blurColor: "rgba(40, 40, 40, 0.5)",
        secondaryText: "rgba(200, 200, 200, 0.7)"
    } as Theme,
    {
        name: 'Chronical',
        bg: "#161617",
        fg: "#3a3a3d",
        tooltip: "#F15025",
        text: "#F1F1F1",
        buttonColor: "rgba(40, 40, 40, 0.3)",
        blurColor: "rgba(40, 40, 40, 0.5)",
        secondaryText: "rgba(200, 200, 200, 0.7)"
    } as Theme,
    {
        name: 'Light',
        bg: "#f8f9fa",
        fg: "#dee2e6",
        tooltip: "#adb5bd",
        text: "#212529",
        buttonColor: "rgba(173, 181, 189, 0.6)",
        blurColor: "rgba(40, 40, 40, 0.5)",
        secondaryText: "#495057"
    } as Theme,
]

let theme = ref(themes[0]);

let showSettings = ref(false);

let currentUser: Ref<User | null> = ref(null);
let currentServer: Ref<Server | null> = ref(null);
let currentChannel: Ref<number | null> = ref(null);

let userIDs = [15, 16];

let users: Ref<User[]> = ref([]);
let servers: Ref<Server[]> = ref([{
            picture: 'data:image;base64, ',
            name: 'Lucas Gouvea Inc.',
            channels: [{
                name: 'Main Channel'
            } as Channel]
        } as Server,
        {
            picture: 'data:image;base64, ',
            name: 'Lucas Gouvea GmbH.',
            channels: [{
                name: 'Main Channel'
            } as Channel,
            {
                name: 'Secondary Channel'
            } as Channel]
        } as Server]);

async function UpdateUsers() {
    await userIDs.forEach(async (n) => {
        users.value.push(await store.dispatch('getUser', n)!);
    })
}

function SelectServer(s: Server) {
    currentServer.value = s;
    currentChannel.value = 0;
}

let loading = ref(true);
let showLogin = ref(true);
let loginPage = ref(0);


async function init() {
    theme.value = themes[await store.dispatch('getStore', 'themeSelected') ?? 0];
    
    let credentials = await store.dispatch('getStore', 'login');
    let thisUser = credentials ? await login(credentials.email, credentials.pwd) : null;
    if (!thisUser) {
        loading.value = false;
        loginPage.value = 1;
    }
    else {
        showLogin.value = false;
    }

    currentUser.value = thisUser;
    UpdateUsers();
    console.log(
        "%cStop!",
        "color:#ff595e;font-family:system-ui;font-size:4rem;font-weight:bold"
    );
    console.log(
        "%cDon't paste anything into here! Scammers notoriously stole personal info through here. Proceed with caution.",
        "color:#1982c4;font-family:system-ui;font-size:1rem;;font-weight:bold"
    );
    await listen('ChatMessageReceived', (event) => {
        // @ts-ignore
        console.log(event.payload!['message']);
    })
}

init()

async function LoginFinished(user: User) {
    currentUser.value = user;
    showLogin.value = false;
}

let selectedUser: Ref<User | null> = ref(null);
const log = console.log;
</script>

<template>
<div :style="{
    '--bg': theme.bg,
    '--fg': theme.fg,
    '--text': theme.text,
    '--button-color': theme.buttonColor,
    '--blur-color': theme.blurColor,
    '--secondary-text': theme.secondaryText,
    '--tooltip': theme.tooltip
}">
<Transition name="fade">
    <div v-if="showLogin" class="login-bg">
        <Login @login="LoginFinished" :loading="loading" :loginPage="loginPage"></Login>
    </div>
</Transition>


<div ref="dropdown" class="user-dropdown">
    <img id="hero" :src="selectedUser?.Hero">
    <img id="pfp" :src="selectedUser?.ProfilePicture">
    <span id="name">{{ selectedUser?.Name }}</span>
    <span id="status">{{ selectedUser?.Status }}</span>
    <button><i class="speech-bubble"></i> Chat</button>
</div>

<div class="wrapper">
    <Settings :style="{
        opacity: showSettings ? 1 : 0,
        'pointerEvents': showSettings ? 'all' : 'none',
        scale: showSettings ? 1 : 1.1
    }"
    
    @close="showSettings = false;"
    
    :themes="themes"
    @themeSelected="(i) => theme = themes[i]"
    ></Settings>

    <main :style="{
        scale: showSettings ? 0.9 : 1,
    }">

        <ServerList @connect="s => SelectServer(s)" :servers="servers"></ServerList>
        <Chat ref="chat" :name="currentServer?.name" :channel="currentServer?.channels[currentChannel!]!" :userClick="showDropdown"></Chat>
        <ChannelList @connect="i => currentChannel = i" :server="currentServer!"></ChannelList>
        <UserList :users="users" :userClick=showDropdown></UserList>
        <div class="current-user">
            <span class="name">{{ currentUser?.Name }}</span>
            <span class="status">{{ currentUser?.Status }}</span>
            <img :src="currentUser?.ProfilePicture">
            <div><i @click="showSettings = !showSettings" class="cogwheel"></i></div>
        </div>
    </main>

</div>
</div>
</template>

